import { useState } from 'react';
import './home.css';
import api from '../../api/api';
import { useEffect } from 'react';

const Home = () => {
  const [character, setCharacter] = useState([]);

  useEffect(() => {
    async function showCharacters() {
      await api
        .get()
        .then((response) => {
          setCharacter(response.data.results.slice(0, 6));
        })
        .catch((err) => {
          console.log('Erro ao mostrar personagem ' + err);
        });
    }

    // showCharacters()
  }, []);

  return (
    <main className="home_bg">
      <div className="container">
        <ul className="list_characters">
          {character.map(() => (
            <li className="character">
              <img src="./image/459.jpeg" alt="" />

              <div className="description">
                <div>
                  <h2 className="name">Butthole Ice Cream Guy</h2>
                  <p className="alive_specie">Alive - Alien</p>
                </div>

                <div>
                  <label htmlFor="">Last Know location:</label>
                  <p className="last_know">Interdimensional Cable</p>
                </div>

                <div>
                  <label htmlFor="">First seen in:</label>
                  <p className="last_know">
                    Interdimensional Cable 2: Tempting Fate
                  </p>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </main>
  );
};

export default Home;
