import { useState } from 'react';
import './home.css';
import api from '../../api/api';
import { useEffect } from 'react';

const Home = () => {
  const [character, setCharacter] = useState([]);
  const [episodes, setEpisodes] = useState([]);

  useEffect(() => {
    async function showCharacters() {
      await api
        .get()
        .then((response) => {
          setCharacter(response.data.results.slice(0, 12));
        })
        .catch((err) => {
          console.log('Erro ao mostrar personagem ' + err);
        });
    }

    async function showEpisode() {
      await api
        .get('https://rickandmortyapi.com/api/episode/')
        .then((response) => {
          setEpisodes([...episodes,response.data.results.slice(0, 12)]);
        })
        .catch((err) => {
          console.log('Erro ao mostrar episodio ' + err);
        });
    }

    showEpisode();
    showCharacters();
  }, []);

  return (
    <main className="home_bg">
      <div className="container">
        <ul className="list_characters">
          {character.map((char) => (
            <li key={char.id} className="character">
              <img src={char.image} alt={char.name} />

              <div className="description">
                <div>
                  <h2 className="name">{char.name}</h2>
                  {char.status === 'Alive' && (
                    <p className="alive_specie alive">
                      {char.status} - {char.species}
                    </p>
                  )}
                  {char.status !== 'Alive' && (
                    <p className="alive_specie dead">
                      {char.status} - {char.species}
                    </p>
                  )}
                </div>

                <div>
                  <label htmlFor="">Last Know location:</label>
                  <p className="last_know">{char.location.name}</p>
                </div>

                <div>
                  <label htmlFor="">First seen in:</label>
                  {episodes.map((ep) => (
                    <p key={ep.id} className="last_know">
                      {ep.name}
                    </p>
                  ))}
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </main>
  );
};

export default Home;
